import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Container, Card, Form, Navbar, Nav } from 'react-bootstrap';

function DoctorDashboard() {
  const [appointments, setAppointments] = useState([]);
  const doctorName = localStorage.getItem('doctorName') || 'Doctor';

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const token = localStorage.getItem('token');
        const userId = localStorage.getItem('userId');
        const res = await axios.get(`http://127.0.0.1:5000/api/user/appointments?doctorId=${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log(res.data);
        if (res.data.success) {
          setAppointments(res.data.data);
        } else {
          toast.error('Failed to fetch appointments');
        }
      } catch (error) {
        console.error(error);
        toast.error('Server Error');
      }
    };

    fetchAppointments();
  }, []);

  const handleChangeStatus = async (appointmentId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        'http://127.0.0.1:5000/api/user/changeStatus',
        {
          appointmentId,
          status: newStatus
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      if (res.data.success) {
        toast.success(res.data.message);
        // Update the appointment status in the local state
        setAppointments(appointments.map(app =>
          app._id === appointmentId ? { ...app, status: newStatus } : app
        ));
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      console.error(error);
      toast.error('Failed to change status');
    }
  };

  return (
    <>
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand href="#home">Doctor Dashboard</Navbar.Brand>
          <Nav className="ml-auto">
            <Nav.Item>
              <Nav.Link href="#profile">{doctorName}</Nav.Link>
            </Nav.Item>
          </Nav>
        </Container>
      </Navbar>
      <Container className="my-5">
        <h1 className="text-center mb-4">Appointments</h1>
        {appointments.length > 0 ? (
          <div>
            {appointments.map((appointment) => (
              <Card key={appointment._id} className="mb-3">
                <Card.Body>
                  <Card.Title>{appointment.patient.name}</Card.Title>
                  <Card.Text>
                    <strong>Date:</strong> {new Date(appointment.date).toLocaleString()}
                  </Card.Text>
                  <Card.Text>
                    <strong>Status:</strong>
                  </Card.Text>
                  <Form.Select 
                    value={appointment.status} 
                    onChange={(e) => handleChangeStatus(appointment._id, e.target.value)} 
                  >
                    <option value="Pending">Pending</option> 
                    <option value="Accepted">Accepted</option>
                    <option value="Rejected">Rejected</option>
                  </Form.Select>
                </Card.Body>
              </Card>
            ))}
          </div>
        ) : (
          <p className="text-center text-muted">No appointments available</p>
        )}
      </Container>
    </>
  );
}

export default DoctorDashboard;
