import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

function PatientDashboard() {
  const [doctors, setDoctors] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [appointmentDate, setAppointmentDate] = useState('');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get('http://127.0.0.1:5000/api/user/doctors');
        if (res.data.success) {
          setDoctors(res.data.data);
        } else {
          toast.error('Failed to fetch doctors');
        }
      } catch (error) {
        console.error(error);
        toast.error('Server Error');
      }
    };

    const fetchAppointments = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://127.0.0.1:5000/api/user/appointments?patientId=${userId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (res.data.success) {
          setAppointments(res.data.data);
        } else {
          toast.error('Failed to fetch appointments');
        }
      } catch (error) {
        console.error(error);
        toast.error('Server Error');
      }
    };

    fetchDoctors();
    fetchAppointments();
  }, [userId]);

  const handleFetchDoctorAppointments = async (doctorId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`http://127.0.0.1:5000/api/user/appointments/by-doctor?doctorId=${doctorId}&patientId=${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data.success) {
        setAppointments(res.data.data);
      } else {
        toast.error('Failed to fetch appointments by doctor');
      }
    } catch (error) {
      console.error(error);
      toast.error('Server Error');
    }
  };

  const handleBookAppointment = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `http://127.0.0.1:5000/api/user/book-appointment`,
        {
          doctorId: selectedDoctor,
          appointmentDate,
          userId
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      if (res.data.success) {
        toast.success(res.data.message);
        setSelectedDoctor(null);
        setAppointmentDate('');
        setAppointments(prev => [...prev, res.data.data]); // Add the new appointment to the state
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      console.error(error);
      toast.error('Booking failed');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Doctors List</h1>
      {doctors.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {doctors.map((doctor) => (
            <li key={doctor._id} style={{ border: '1px solid #ccc', margin: '10px 0', padding: '10px', borderRadius: '5px' }}>
              <h2 style={{ margin: '5px 0' }}>{doctor.name}</h2>
              <p style={{ margin: '5px 0' }}><strong>Specialty:</strong> {doctor.specialty}</p>
              <p style={{ margin: '5px 0' }}><strong>Email:</strong> {doctor.email}</p>
              <button 
                style={{ padding: '5px 10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '3px', cursor: 'pointer' }}
                onClick={() => {
                  setSelectedDoctor(doctor._id);
                  handleFetchDoctorAppointments(doctor._id);
                }}
              >
                View Appointments
              </button>
              <button 
                style={{ padding: '5px 10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '3px', cursor: 'pointer', marginLeft: '10px' }}
                onClick={() => setSelectedDoctor(doctor._id)}
              >
                Book Appointment
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p style={{ textAlign: 'center', color: '#999' }}>No doctors available</p>
      )}

      {selectedDoctor && (
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <h2 style={{ color: '#333' }}>Book Appointment</h2>
          <input
            type="datetime-local" // Changed to allow date and time selection
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
            style={{ padding: '5px', borderRadius: '3px', border: '1px solid #ccc', marginBottom: '10px' }}
          />
          <br />
          <button 
            onClick={handleBookAppointment}
            style={{ padding: '5px 10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '3px', cursor: 'pointer', marginRight: '10px' }}
          >
            Confirm
          </button>
          <button 
            onClick={() => setSelectedDoctor(null)}
            style={{ padding: '5px 10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '3px', cursor: 'pointer' }}
          >
            Cancel
          </button>
        </div>
      )}

      <h1 style={{ textAlign: 'center', color: '#333', marginTop: '20px' }}>Your Appointments</h1>
      {appointments.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {appointments.map((appointment) => (
            <li key={appointment._id} style={{ border: '1px solid #ccc', margin: '10px 0', padding: '10px', borderRadius: '5px' }}>
              <p style={{ margin: '5px 0' }}><strong>Date:</strong> {appointment.date ? new Date(appointment.date).toLocaleString() : 'No date'}</p> {/* Added defensive check */}
              <p style={{ margin: '5px 0' }}><strong>Doctor:</strong> {appointment.doctor?.name || 'Unknown'}</p> {/* Added optional chaining */}
              <p style={{ margin: '5px 0' }}><strong>Status:</strong> {appointment.status ? 'Pending' : 'Accepted'}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p style={{ textAlign: 'center', color: '#999' }}>No appointments available</p>
      )}
    </div>
  );
}

export default PatientDashboard;
