import React, { useEffect, useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

function Patient() {
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [appointmentDate, setAppointmentDate] = useState('');

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get('http://127.0.0.1:5000/api/user/doctors');
        if (res.data.success) {
          setDoctors(res.data.data);
        } else {
          toast.error('Failed to fetch doctors');
        }
      } catch (error) {
        console.error(error);
        toast.error('Server Error');
      }
    };

    fetchDoctors();
  }, []);

  const handleBookAppointment = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://127.0.0.1:5000/api/user/book-appointment', 
        {
          doctorId: selectedDoctor,
          appointmentDate,
        }, 
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      if (res.data.success) {
        toast.success(res.data.message);
        setSelectedDoctor(null);
        setAppointmentDate('');
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      console.error(error);
      toast.error('Booking failed');
    }
  };

  return (
    <div>
      <h1>Doctors List</h1>
      {doctors.length > 0 ? (
        <ul>
          {doctors.map((doctor) => (
            <li key={doctor._id}>
              <h2>{doctor.name}</h2>
              <p>{doctor.specialty}</p>
              <p>{doctor.email}</p>
              <button onClick={() => setSelectedDoctor(doctor._id)}>
                Book Appointment
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No doctors available</p>
      )}

      {selectedDoctor && (
        <div>
          <h2>Book Appointment</h2>
          <input
            type="date"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
          />
          <button onClick={handleBookAppointment}>Confirm</button>
          <button onClick={() => setSelectedDoctor(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default Patient;
